import { Checkbox } from "@mui/material";
import { useContext } from "react";
import { FormContext } from "../../../context/FormContext";

export default function Step3() {
  const { user, setUser } = useContext(FormContext);
  return (
    <div className="step-3-main p-10 flex flex-col gap-y-6">
      <div className="step-3-title">
        <h3 className="text-[var(--marine-blue)] text-2xl font-[700]">
          Pick add-ons
        </h3>
        <span className="text-sm text-[var(--cool-gray)]">
          Add-ons help enchance your gaming experience.
        </span>
      </div>
      <div className="step-3-add-ons flex flex-col gap-y-3">
        <div
          onClick={() => {
            setUser((user) => {
              return {
                ...user,
                addOns: {
                  ...user.addOns,
                  addon1: !user.addOns.addon1,
                },
              };
            });
          }}
          className="add-on-online-service select-none flex flex-row items-center justify-start gap-x-5 p-3 cursor-pointer transition-all duration-500 hover:bg-[var(--magnolia)] rounded-xl bg-[var(--white)] border-marine"
        >
          <Checkbox onClick={alert("x")} checked={user.addOns.addon1} />
          <div className="online-service-description">
            <h3 className="text-[var(--marine-blue)] text-base font-[700]">
              Online service
            </h3>
            <span className="text-sm text-[var(--cool-gray)]">
              Access to multiplayer games
            </span>
          </div>
          <div className="online-service-price ml-auto  text-[var(--purplish-blue)] text-[14px]">
            <span>+1$/mo</span>
          </div>
        </div>

        <div
          onClick={() => {
            setUser((user) => {
              return {
                ...user,
                addOns: {
                  ...user.addOns,
                  addon2: !user.addOns.addon2,
                },
              };
            });
          }}
          className="add-on-larger-storage select-none flex flex-row items-center justify-start gap-x-5 p-3 cursor-pointer transition-all duration-500 hover:bg-[var(--magnolia)] rounded-xl bg-[var(--white)] border-marine"
        >
          <Checkbox />
          <div className="larger-storage-description">
            <h3 className="text-[var(--marine-blue)] text-base font-[700]">
              Larger-storage
            </h3>
            <span className="text-sm text-[var(--cool-gray)]">
              Extra 1TB of cloud save
            </span>
          </div>
          <div className="larger-storage-price ml-auto text-[var(--purplish-blue)] text-[14px]">
            <span>+1$/mo</span>
          </div>
        </div>

        <div
          onClick={() => {
            setUser((user) => {
              return {
                ...user,
                addOns: {
                  ...user.addOns,
                  addon3: !user.addOns.addon3,
                },
              };
            });
          }}
          className="add-on-customizable-profile select-none flex flex-row items-center justify-start gap-x-5 p-3 cursor-pointer transition-all duration-500 hover:bg-[var(--magnolia)] rounded-xl bg-[var(--white)] border-marine"
        >
          <Checkbox />
          <div className="customizable-profile-description">
            <h3 className="text-[var(--marine-blue)] text-base font-[700]">
              Customizable-profile
            </h3>
            <span className="text-sm text-[var(--cool-gray)]">
              Custom theme on your profile
            </span>
          </div>
          <div className="customizable-profile-price ml-auto text-[var(--purplish-blue)] text-[14px]">
            <span>+2$/mo</span>
          </div>
        </div>
      </div>

      <div className="step-2-actions mt-10 flex flex-row items-center justify-between w-full">
        <button className="text-[var(--marine-blue)] transition-all duration-500 p-3 rounded-xl hover:bg-[var(--magnolia)]">
          Go Back
        </button>
        <button className="form-button">Next Step</button>
      </div>
    </div>
  );
}
