import { useContext, useEffect } from "react";
import { FormContext } from "../../context/FormContext";

export default function Switch() {
  const { planTime, setPlanTime } = useContext(FormContext);
  const switchToggle = () => {
    if (planTime === "monthly") {
      setPlanTime("monthly");
      const SwitchBall = document.querySelector(".switch-ball");
      SwitchBall.classList.remove("switchToggle");
    } else if (planTime === "yearly") {
      setPlanTime("yearly");
      const SwitchBall = document.querySelector(".switch-ball");
      SwitchBall.classList.add("switchToggle");
    }
  };

  return (
    <div
      onClick={switchToggle}
      className="switch bg-[var(--marine-blue)] h-[24px] rounded-xl w-[50px] relative cursor-pointer"
    >
      <div className="switch-ball h-[15px] w-[15px] rounded-full bg-[var(--white)] transition-all duration-300 absolute top-1 right-1"></div>
    </div>
  );
}
