import { Form, Formik } from "formik";
import * as Yup from "yup";

export default function Step1() {
  return (
    <div className="step1-main">
      <div className="step-1-title">
        <h3>Personel info</h3>
        <span>Please provide your name, email address, and phone number.</span>
      </div>
      <div className="step-1-form">
        <Formik
          initialValues={{
            name: "",
            mail: "",
            phone: 0,
          }}
          validationSchema={Yup.object({
            name: Yup.string().required("This field is required"),
            mail: Yup.string().required("This field is required"),
            phone: Yup.number().required("This field is required"),
          })}
        >
          {({ dirty, handleChange, handleSubmit, errors, values, touched }) => {
            return <Form onSubmit={handleSubmit}></Form>;
          }}
        </Formik>
      </div>
    </div>
  );
}
