import { useContext } from "react";
import { FormContext } from "../../context/FormContext";
import Steps from "./Steps";

export default function Layout() {
  const { step, content } = useContext(FormContext);
  return (
    <>
      {content.map((c) => {
        if (c.number === step) {
          return c.component;
        }
      })}
    </>
  );
}
