export default function Switch() {
  const switchToggle = () => {
    const SwitchBall = document.querySelector(".switch-ball");
    SwitchBall.classList.toggle("switchToggle");
  };
  return (
    <div className="switch bg-[var(--marine-blue)] h-[25px] rounded-xl w-[50px] relative cursor-pointer">
      <div className="switch-ball h-[15px] w-[15px] rounded-full bg-[var(--white)] transition-all duration-300 absolute top-1 left-1"></div>
    </div>
  );
}
