import { useContext } from "react";
import { FormContext } from "../../context/FormContext";

export default function Layout() {
  const { stepTitles } = useContext(FormContext);
  console.log(stepTitles);
  return (
    <div className="multi-step-main w-[800px] h-[510px] shadow-xl p-3 bg-[var(--white)] rounded-xl flex flex-row">
      <div className="steps w-[30%] h-[100%]">
        {stepTitles.map((stepTitle) => {
          return <div className={`step-${stepTitle.number}`}></div>;
        })}
      </div>
      <div className="content w-[70%] h-[full]"></div>
    </div>
  );
}
