import { Checkbox } from "@mui/material";

export default function Step3() {
  return (
    <div className="step-3-main p-10">
      <div className="step-3-title">
        <h3 className="text-[var(--marine-blue)] text-2xl font-[700]">
          Pick add-ons
        </h3>
        <span className="text-sm text-[var(--cool-gray)]">
          Add-ons help enchance your gaming experience.
        </span>
      </div>
      <div className="step-3-add-ons">
        <div className="add-on-online-service">
          <Checkbox
            checked={checked}
            onChange={handleChange}
            inputProps={{ "aria-label": "controlled" }}
          />
        </div>
      </div>
    </div>
  );
}
