import { useState } from "react";

export default function Step2() {
  const [planTime, setPlanTime] = useState("monthly");
  return (
    <div className="step-2-main flex flex-col">
      <div className="step-2-titles">
        <h3>Select Your Plan</h3>
        <span>You have the option of monthly or yearly billing.</span>
      </div>
      <div className="step-2-options">
        <div className="option-arcade">
          <div className="arcade-image">
            <img src={require("../../../images/icon-arcade.svg").default}></img>
          </div>
          <div className="arcade-description">
            <h3>Arcade</h3>
            <span>$9/mo</span>
          </div>
        </div>
      </div>
    </div>
  );
}
